import React, { useState } from "react";
import "./Sales.css";
import {TextItem} from "./TextItem";

export default function App() {


  const [textSelected, setTextSelected] = useState("");

  const selectLi = (id) => {
    let index = TextItem.findIndex((x) => x.id === id);
    if (index === -1) return;
    setTextSelected(TextItem[index].value);
  };
  return (
    <>  
    <div className="Container">
        <div className="title"><h2>Le cardigan durable V2</h2></div>
        <div className="price"><p>â‚¬112</p><p><a href= "#">Quelle est ma taille ?</a></p></div>
         <div className="MenuItems">
      <ul>
        {TextItem.map((x) => (
          <li
            onClick={() => {
              selectLi(x.id);
            }}
          >
            {x.id}
          </li>
        ))}
      </ul>
      </div>
      <div>{textSelected}</div>
      </div>
    </>
  );
}
